document.addEventListener("DOMContentLoaded", () => {
    const btnMenu = document.querySelector(".btn-menu");
    const menu = document.getElementById("menu");
    const menuIcon = document.querySelector("#menu .btn-menu i");

    btnMenu.addEventListener("click", () => {
        menu.classList.toggle("hide");
        menuIcon.classList.toggle("fa-bars");
        menuIcon.classList.toggle("fa-times");
    });

    document.addEventListener("click", (event) => {
        const target = event.target;
        if (!target.closest("#menu") && !target.closest(".btn-menu")) {
            if (!menu.classList.contains("hide")) {
                menu.classList.add("hide");
                menuIcon.classList.remove("fa-times");
                menuIcon.classList.add("fa-bars");
            }
        } else if (target.closest("#menu a")) {
            menu.classList.add("hide");
            menuIcon.classList.remove("fa-times");
            menuIcon.classList.add("fa-bars");
        }
    });

    setTimeout(() => {
        const reviewButton = document.querySelector(".ButtonBase__Ellipsis-sc-11c179a3-5.haApKJ");
        if (reviewButton) {
            reviewButton.textContent = "Agregar reseÃ±a";
            reviewButton.style.color = "#ffffff";
        }

        const ekQXgElements = document.querySelectorAll(".EkQXg");
        ekQXgElements.forEach(el => {
            el.style.background = "#fff";
            el.style.boxShadow = "0 0 5px #0003";
            el.style.margin = "5px";
        });

        const dIBzjhElements = document.querySelectorAll(".dIBzjh");
        dIBzjhElements.forEach(el => {
            el.style.background = "#fff !important";
        });

        const elfsightApp = document.querySelector(".elfsight-app-c258c807-c999-48fa-acb4-990dd3b6d0b5.eapps-widget.eapps-widget-show-toolbar > a");
        if (elfsightApp) {
            elfsightApp.style.display = "none !important";
            elfsightApp.style.opacity = "0 !important";
        }
    }, 4000);
});