document.addEventListener("DOMContentLoaded", function () {
    const content = document.getElementById("content");
    const slider = document.getElementById("slider");
    const bodyBG = document.querySelector(".bodyBG");

    // Inicializar el scroll inverso correctamente
    slider.scrollTop = slider.scrollHeight;

    content.addEventListener("scroll", function () {
        const scrollTop = content.scrollTop;
        const contentScrollHeight = content.scrollHeight - content.offsetHeight;
        const sliderScrollHeight = slider.scrollHeight - slider.offsetHeight;
        const bodyScrollWidth = bodyBG.scrollWidth - bodyBG.offsetWidth;

        // Asegurarse de que los c√°lculos sean proporcionales y no se dupliquen
        const sliderScrollPosition = sliderScrollHeight - (scrollTop * sliderScrollHeight / contentScrollHeight);
        const bodyScrollPosition = scrollTop * bodyScrollWidth / contentScrollHeight;

        // Aplicar los valores calculados
        slider.scrollTop = Math.max(0, Math.min(sliderScrollHeight, sliderScrollPosition));
        bodyBG.scrollLeft = Math.max(0, Math.min(bodyScrollWidth, bodyScrollPosition));
    });
});