$(document).ready(function () {
    let l = $("#content"),
        o = $("#slider"),
        t = $(".bodyBG"); o.scrollTop(o[0].scrollHeight),
            l.on("scroll",
                function () {
                    let c = l.scrollTop(),
                        e = l[0].scrollHeight - l.height(),
                        r = o[0].scrollHeight - o.height(),
                        i = t[0].scrollWidth - t.width(); o.scrollTop(r - c * r / e),
                            t.scrollLeft(c * i / e)
                })
});
