document.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("content"),t=document.getElementById("slider"),l=document.querySelector(".bodyBG");t.scrollTop=t.scrollHeight,e.addEventListener("scroll",function(){let o=e.scrollTop,n=e.scrollHeight-e.offsetHeight,s=t.scrollHeight-t.offsetHeight,i=l.scrollWidth-l.offsetWidth;t.scrollTop=Math.max(0,Math.min(s,s-o*s/n)),l.scrollLeft=Math.max(0,Math.min(i,o*i/n))})});